import decode from"jwt-decode";class AuthService{getProfile(){return decode(this.getToken())}loggedIn(){const e=this.getToken();return!!e&&!this.isTokenExpired(e)}isTokenExpired(e){try{return decode(e).exp<Date.now()/1e3}catch(e){return!1}}getToken(){return localStorage.getItem("id_token")}login(e){localStorage.setItem("id_token",e),window.location.assign("/")}logout(){localStorage.removeItem("id_token"),window.location.assign("/")}}export default new AuthService;